// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract CourseManagement {
    struct Course {
        uint256 id;
        string name;
        address payable ownerAddress;
        uint256 price;
        uint256[] students; // all buyers of a course
        string imgURL;
    }

    //buyer
    struct Student {
        uint256 id;
        string name;
        uint256[] courses; //students owned courses
    }

    //seller
    struct Instructor {
        address id;
        string name;
        uint256[] courses; //instructors uploaded courses
    }
    event courseAdded(string message);
    mapping(uint256 => Course) courses;
    mapping(address => Instructor) instructors;
    mapping(address => Student) students;

    address[] public instructorAccts;
    uint256[] public allcourses;
    address[] public studentsAccts;
    address owner;

    constructor() public {
        owner = msg.sender;
    }

    function addInstrcutor(address _address,  string memory _fullname, uint256[] memory _courses) public {
    
    instructors[_address] = Instructor(
        {
            id: _address,
            name: _fullname,
            courses: _courses
          
        }
    );
    
    instructorAccts.push(_address);
}

